<?phpnamespace App\Http\Controllers\home;use Illuminate\Http\Request;use DB;use App\Http\Requests;use App\Http\Controllers\Controller;use Illuminate\Routing\Route;use Cache;use App\Common\Ucpaas;use Hash;class HomeController extends Controller{    /**     * 首页方法     * @param     * @return null     */    public function index()    {        return view('home.index');    }    /**     * 前台登录页面     * @return     */    public function getLogin(Request $request)    {        return view('home.login.Login');    }    /**     * 用户名登录     * @param Request $request     * @return int     *  1 是为空     *  2 手机号未注册     *  3 被封号     *  4 密码错误     *  5 登录成功 跳转到主页     */    public function postLogina(Request $request)    {        //获取用户的登录信息        $user = $_POST;        //判断用户名或密码是否为空        if(empty($user['username1']) || empty($user['password1'])){            return 1;        }        //与数据库进行对比        $res = DB::table('user')->where('username','=',$user['username1'])->get();        //判断用户是否存在        if(empty($res)){            return 2;        }else{           if(Hash::check($user['password1'],$res[0]->password)){               //判断用户是被禁止登陆               if($res[0]->status == 1){                   return 3;               }               //将登陆用户的信息存入session               $request->session()->put(['home'=>$res[0]]);               //解析主页               return 5;           }else{               return 4;           }        }    }    /**     * 用户手机号登录     * @param Request $request     * @return int     *  1 是为空     *  2 手机号未注册     *  3 被封号     *  5 登录成功 跳转到主页     */    public function postLoginb(Request $request)    {        //获取用户的登录信息        $user = $_POST;        //判断用户名或密码是否为空        if(empty($user['userphone'])){            return 1;        }        //与数据库进行对比        $res = DB::table('user')->where('userphone','=',$user['userphone'])->get();        //判断手机号是否存在        if(empty($res)){            return 2;        }else{            //判断用户是被禁止登陆            if($res[0]->status == 1){                return 3;            }            //将登陆用户的信息存入session            $request->session()->put(['home'=>$res[0]]);            //解析主页            return 5;        }    }    /**     * 前台注册页面     * @return     */    public function getRegister()    {        return view('home.login.Register');    }    /**     * 获取用户名 判断用户名是否重复     * @param Request $request     * @return int     *  1 不重名     *  2 用户名已存在     */    public function getRegisterb(Request $request)    {        //获取用户名        $name = $_GET['name'];        //查询数据库是否有重名        $res = DB::table('user')->where('username','=',$name)->get();        if(empty($res)){            return 1;        }else{            return 2;        }    }    /**     * 查询手机号是否注册过     * @param Request $request     * @return int     *  1 未注册     *  2 已注册     */    public function getPhone(Request $request)    {        //获取要查询的手机号        $c = $request->input('c');        //查询数据库判断手机号是否重复        $b = DB::table('user')->where('userphone','=',$c)->get();        if(empty($b)){            return 1;        }else{            return 2;        }    }    /**     * 注册时手机号发送短信     * @param Request $request     * @return array     */    public function getYanzheng(Request $request)    {        //获取要发送的手机号        $a = $request->input('phone');        //短信验证码        //初始化必填        $options['accountsid']='7ac5387ae559b5a4a547c5b5ae9ca072';        $options['token']='46502cc72314f98d1a6d3a542e84da6a';        //初始化 $options必填        $ucpass = new Ucpaas($options);        $appId = "0abae7bf13774e22a47710cd5c268814";        $to = $a;        $templateId = "39612";        $code = rand(10000,99999);        $param=$code;        return [$ucpass->templateSMS($appId,$to,$templateId,$param),$code];    }    /**     * 处理用户注册信息     * @param Requests\HomePostRequest $request     * @return $this|\Illuminate\Http\RedirectResponse|\Illuminate\Routing\Redirector     */    public function postRegisterb(\App\Http\Requests\HomePostRequest $request)    {        //获取所有提交的数据        $user = $request->all();        //查询名字和电话是否重复        $a = DB::table('user')->where('username','=',$user['username'])->get();        $b = DB::table('user')->where('userphone','=',$user['userphone'])->get();        //判断用户名是否重复 和两次密码是否一致        if(!empty($a)){            return view('home.login.Register')->withErrors('用户名重复,请更换');        }else if(!empty($b)){            return view('home.login.Register')->withErrors('手机号重复,请更换');        }else if($user['password'] != $user['password2']){            return view('home.login.Register')->withErrors('两次密码不一样');        }        //去掉无用字段        $user = $request->except('_token','password2');        //哈希加密        $user['password']=Hash::make($user['password']);        //加入时间        $user['jointime']=time();        //将信息插入数据库        $res = DB::table('user')->insertGetId($user);        //判断        if($res){            return redirect('home/login');        }else{            return back()->withErrors('注册失败');        }    }    /**     * 详情页方法     * @param     * @return     */    public function getDetails()    {        return view('home.details');    }    /**     * 头部列表     * @param     * @return     */    public function getHead()    {        return view('home.headlist');    }    /**     * 列表页方法     * @param     * @return     */    public function getList(Request $request)    {        //用户列表页        $id = $request->input('id');        if(!empty("a"))        {            // DB::connection()->enableQueryLog();            $shop = DB::table('shop as s')                ->join('shop_comment as sc','s.s_id','=','sc.s_id')                ->where('s.st_id',$id)                ->paginate(2);            // dd(DB::getQueryLog());        }        switch($request->input('a')){            case "default":                $shop = DB::table('shop as s')                    ->join('shop_comment as sc','s.s_id','=','sc.s_id')                    ->where('s.st_id',$id)                    ->paginate(2);                break;            case "sole":                $shop = DB::table('shop as s')                    ->join('shop_comment as sc','s.s_id','=','sc.s_id')                    ->where('s.st_id',$id)                    ->orderBy('Sales', 'desc')                    ->paginate(2);                break;            case "praise":                // DB::connection()->enableQueryLog();                $shop = DB::table('shop as s')                    ->join('shop_comment as sc','s.s_id','=','sc.s_id')                    ->where('s.st_id',$id)                    ->orderby('sc.star','desc')                    ->paginate(2);                // dd(DB::getQueryLog());                break;            case "newtime":                $shop = DB::table('shop as s')                    ->join('shop_comment as sc','s.s_id','=','sc.s_id')                    ->where('s.st_id',$id)                    ->orderBy('uptime', 'desc')                    ->paginate(2);                break;        }        //商品库存        $shopsales = DB::table('shop')            ->where('st_id',$id)            ->value('Sales');        //类别名查询        $titletype=DB::table('shop_type')            ->where('st_id',$id)            ->value('stname');        //获取所有的请求参数        $all = $request->all();        //解析模板        return view('home.list',['id'=>$id,'shopsales'=>$shopsales,'titletype'=>$titletype,'shop'=>$shop ,'all'=>$all]);    }   }